package Java_Exercicios;

public class ProjetoVariaveis {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =1;
		for(int i =0;i<3;i++) {
			int y = 1;
			System.out.print("Valor de y: "+y);
			System.out.println(" Valor de x: "+x);
			y++;
			x++;
		}
		
	}

}
